(37, 3, 'LUIZ CARLOS MACHADO', '1990-12-13', '12.745.824-3', '086.188.549-05', '(44) 99829-9659', '(44) 99990-9133', 'contato.luizcarlosmachado@gmail.com', '', '85440-000', 'PR', 'RUA VEREADOR JOÃO PALMA', '55', 'JARDIM PANORAMA', 'UBIRATÃ', 'sertanejo', 'FORÇA ESTRANHA', 'CAETANO VELOSO', 'ROBERTO CARLOS', 'http://bit.ly/luizcarlosmachado-festibema', 'https://www.youtube.com/watch?v=Ktqr1ksJ7Jw', '<h1><span style="font-size:28px">For&ccedil;a Estranha</span></h1>\r\n\r\n<h2><span style="font-size:20px">ROBERTO CARLOS</span></h2>\r\n\r\n<p><strong><span style="font-size:18px">Compositor: Caetano Veloso</span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Eu vi um menino correndo </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Eu vi o tempo </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Brincando ao redor do caminho daquele menino </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Eu pus os meus p&eacute;s no riacho </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">E acho que nunca os tirei </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">O sol ainda brilha na estrada </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">E eu nunca passei </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Eu vi a mulher preparando </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Outra pessoa </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">O tempo parou pra eu olhar para aquela barriga </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">A vida &eacute; amiga da arte </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">&Eacute; a parte que o sol me ensinou </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">O sol que atravessa essa estrada </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Que nunca passou </span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style="font-size:16px">Por isso uma for&ccedil;a me leva a cantar </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Por isso essa for&ccedil;a estranha no ar </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Por isso &eacute; que eu canto, n&atilde;o posso parar </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Por isso essa voz tamanha </span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style="font-size:16px">Eu vi muitos cabelos brancos </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Na fronte do artista </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">O tempo n&atilde;o p&aacute;ra, e no entanto ele nunca envelhece </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Aquele que conhece o jogo </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Do fogo das coisas que s&atilde;o </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">&Eacute; o sol, &eacute; o tempo, &eacute; a estrada </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">&Eacute; o p&eacute; e &eacute; o ch&atilde;o </span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style="font-size:16px">Eu vi muitos homens brigando </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Ouvi seus gritos </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">Estive no fundo de cada vontade encoberta </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">E a coisa mais certa de todas as coisas </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">N&atilde;o vale um caminho sob o sol </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">&Eacute; o sol sobre a estrada </span></strong></p>\r\n\r\n<p><strong><span style="font-size:16px">&Eacute; o sol sobre a estrada, &eacute; o sol </span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style