(93, 9, 'Clezielle & Tiago', '0000-00-00', '', '', '', '', '', '', '', 'PR', '', '', '', 'Três Barras do Paraná', 'sertanejo', 'Se não for por amor', '', 'Wilson & Soraia ', '', '', '<p>Eu n&atilde;o quero me perder de novo<br />\r\nN&atilde;o quero me machucar de novo<br />\r\nN&atilde;o quero n&atilde;o<br />\r\nEu preciso proteger meu cora&ccedil;&atilde;o<br />\r\nEu n&atilde;o quero te entregar meu corpo<br />\r\nPra depois me arrebentar de novo<br />\r\nNa solid&atilde;o<br />\r\nTenho medo de embarcar noutra ilus&atilde;o<br />\r\nTira os olhos dos meus olhos<br />\r\nQue eu n&atilde;o sou de a&ccedil;o<br />\r\nAntes que eu enlouque&ccedil;a<br />\r\nE caia nos seus bra&ccedil;os<br />\r\nN&atilde;o me tente desse jeito<br />\r\nPara com esse jogo<br />\r\nSe n&atilde;o vou me queimar<br />\r\nNo seu fogo<br />\r\nSe n&atilde;o for por amor<br />\r\n&Eacute; melhor n&atilde;o tentar me seduzir<br />\r\nEu n&atilde;o sei se vai dar pra resistir<br />\r\nA carne &eacute; fraca e voc&ecirc; &eacute; demais<br />\r\nSe n&atilde;o for por amor<br />\r\nPara de provocar meu cora&ccedil;&atilde;o<br />\r\nJ&aacute; sofri, j&aacute; chorei desilus&atilde;o<br />\r\nBrilhante f&aacute;cil eu n&atilde;o quero mais<br />\r\nDiga que n&atilde;o vai brincar<br />\r\nSe me disser que &eacute; por<br />\r\nAmor eu vou te amar<br />\r\nSe n&atilde;o for por amor...</p>\r\n'),
(94, 9, 'Michel Marks', '0000-00-00', '', '', '', '', '', '', '', 'PR', '', '', '', 'Quedas do Iguaçú', 'sertanejo', 'Por te amar de mais', '', 'Bruno & Marrone', '', '', '<p>Eu j&aacute; sei<br />\r\nSei que vai ligar querendo me encontrar<br />\r\nMas sei que vou estar aqui<br />\r\nVoc&ecirc; fez meu cora&ccedil;&atilde;o chorar<br />\r\nSe foi sem avisar<br />\r\nNem deu valor no que eu senti<br />\r\nCuidado o que se faz se paga aqui<br />\r\nO nosso amor n&atilde;o vai ficar assim</p>\r\n\r\n<p>Se for sonhar, se for sofrer<br />\r\nSe apaixonar e algu&eacute;m magoar voc&ecirc;<br />\r\nSe for chorar, se for sofrer<br />\r\nVai se lembrar que eu amo voc&ecirc;</p>\r\n\r\n<p>Eu j&aacute; sei<br />\r\nSei que vai ligar querendo me encontrar<br />\r\nE sei que vou estar aqui<br />\r\nVoc&ecirc; fez meu cora&ccedil;&atilde;o chorar<br />\r\nSe foi sem avisar<br />\r\nNem deu valor no que eu senti<br />\r\nCuidado o que se faz se paga aqui<br />\r\nO nosso amor n&atilde;o vai ficar assim</p>\r\n\r\n<p>Se for sonhar, se for querer<br />\r\nSe apaixonar e algu&eacute;m magoar voc&ecirc;<br />\r\nSe for chorar, se for sofrer<br />\r\nVai se lembrar que eu amo voc&ecirc;</p>\r\n\r\n<p>Te perdoo outra vez<br />\r\nEsque&ccedil;o tudo que me fez<br />\r\nPor amor, por te amar demais<br />\r\nPois quem ama fica assim<br />\r\nE o amor est&aacute; em mim<br />\r\nVem trazer de volta a minha paz</p>\r\n\r\n<p>Se for sonhar, se for querer<br />\r\nSe apaixonar e algu&eacute;m magoar voc&ecirc;<br />\r\nSe for chorar, se for sofrer<br />\r\nVai se lembrar que eu amo voc&ecirc;</p>\r\n'),
